import "./TodayWeatherCard.css";

function TodayWeatherCard(props) {
    const renderDate = (date) => {
        const options = { day: '2-digit', year: 'numeric', month: 'short', weekday: 'long', hour: '2-digit', minute: '2-digit' };
        const formattedDate = new Intl.DateTimeFormat('en-US', options).format(new Date(date));
        return formattedDate;
    }

    const renderDetails = (data) => {
        const { icon, details } = data;
        return (
            <div>
                <img src={icon} alt={details} />
                <p>{details}</p>
            </div>
        )
    }

    const {
        date,
        city,
        weather,
        water,
        wind,
        sun,
        minTemp,
        maxTemp
    } = props.data;

    return (
        <div className="today-weather-card">
            <p>{renderDate(date)}</p>

            <h2>{city}</h2>

            <h2>{`${minTemp}°C`}</h2>
            <p>{`${minTemp} ~ ${maxTemp}`}</p>

            <p>{weather}</p>
            <div className="details">
                <div>{renderDetails('../../../assets/meta_icon/humidity.png', water)}</div>
                <div>{renderDetails('../../../assets/meta_icon/wind_speed.png', wind)}</div>
                <div>{renderDetails('../../../assets/meta_icon/PM2.5.png', sun)}</div>
                <div>{renderDetails('../../../assets/meta_icon/So.png', minTemp)}</div>
            </div>
        </div>
    )
}

export default TodayWeatherCard;
