import "./TodayWeatherCard.css";

function TodayWeatherCard(props) {
    const renderDate = (date) => {
        const options = { day: '2-digit', month: 'short', weekday: 'long', hour: '2-digit', minute: '2-digit' };
        const formattedDate = new Intl.DateTimeFormat('en-US', options).format(new Date(date));
        return formattedDate;
    }

    const renderDetails = (data) => {
        const { icon, details } = data;
        return (
            <div>
                <img src={icon} alt={details} />
                <p>{details}</p>
            </div>
        )
    }

    const {
        date,
        city,
        weather,
        water,
        wind,
        sun,
        minTemp,
        maxTemp
    } = props.data;

    return (
        <div className="today-weather-card">
            <p>{date}</p>
            <h2>{city}</h2>

            <h2>{water}</h2>
            <h2>{wind}</h2>
            <h2>{sun}</h2>
            <div>{`${minTemp} ~ ${maxTemp}`}</div>

            <h2>{weather}</h2>
            <div>
                <div>{renderDetails('water', water)}</div>
                <div>{renderDetails('wind', wind)}</div>
                <div>{renderDetails('sun', sun)}</div>
                <div>{renderDetails('temperature', minTemp)}</div>
            </div>
        </div>
    )
}

export default TodayWeatherCard;
